            date:this.props.date,
            img:this.props.img
        }
    }
    render(){
        var detail=this.state.detail.split('\n').map(s=>{return(<span>{s}<br/></span>)});
        const div = <div className={itemWrap}> <div className={'item'}><div className={'item_kind'}>{this.state.kind}</div><div className={'item_picture'}><img src={this.state.img}/></div>
            <div className={'item_detail'}><div className={'item_detail_detail'}>{detail}</div><div className={'item_detail_date'}>{this.state.date}</div></div></div></div>;
        return(div);
    }
}

export default Item;
