import React from 'react';
import Item from './item.js';

class Body extends React.Component{
    constructor(props){
        super(props);
        this.state= {
            order_number:0,
            next_order_number:1,
            main_item: [
                {
                    key:0,
                    number: "01/05",
                    kind: "Innovation\nRetail\nSpace\nBrand",
                    detail_detail: "The retail campus for\nmodern creators and\nconsumers.",
                    detail_title: "Gather at\nFactory.",
                    image: "https://d2yaqzrk61txq2.cloudfront.net/system/uploads/article/image/000/000/022/Factory-website1.jpg"
                },
                {
                    key:1,
                    number: "02/05",
                    kind: "Brand\nSpace",
                    detail_detail: "Leveraging space and\nhow people move\nthrough it to design a\nunique and impactful\ncampaign.",
                    detail_title: "Moving\npeople &\nmoving the\nmark.",
                    image: "https://d2yaqzrk61txq2.cloudfront.net/system/uploads/article/image/000/000/019/ATB-Airport-Space-8.jpg"
                },
                {
                    key:2,
                    number: "03/05",
                    kind: "Brand",
                    detail_detail: "How smaller brands can\nuse packaging to tell\ntheir brand story and\ncapture market share.",
                    detail_title: "The medium is\nthe message.",
                    image: "https://d2yaqzrk61txq2.cloudfront.net/system/uploads/article/image/000/000/001/ACE-packaging.jpeg"
                },
                {
                    key:3,
                    number: "04/05",
                    kind: "Space",
                    detail_detail: "Creating comprehensive\ndining experience\nthrough cohesive design.",
                    detail_title: "You eat with\nyour eyes first.",
                    image: "https://d2yaqzrk61txq2.cloudfront.net/system/uploads/article/image/000/000/004/4_Tokiwa1-resturant-min.jpg"
                },
                {
                    key:4,
                    number: "05/05",
                    kind: "Space",
                    detail_detail: "How we captured ATB's\nbrand story through\nphysical space design.",
                    detail_title: "Transactions\nto interactions\nwith ATB.",
                    image: "https://d2yaqzrk61txq2.cloudfront.net/system/uploads/article/image/000/000/009/ATB8Avenue1-spaces.jpg"
                }
            ]

        }
        setInterval(function(){
            let oN;
            let nN=this.state.next_order_number;
                if(oN===3){
                    nN=0;
                    oN=4;
                }
                else if(oN===4){
                    nN=1;
                    oN=0;
                }
                else{
                    oN++;
                    nN++;
                }
                this.setState({order_number:oN,next_order_number:nN});
            },5000);
    }
    lineChange(s){
        return(s.split("\n").map(v=>{return(<span>{v}<br/></span>)}))
    }
    render(){
        const kind=this.lineChange(this.state.main_item[this.state.order_number].kind);
        const detail_detail=this.lineChange(this.state.main_item[this.state.order_number].detail_detail);
        const detail_title=this.lineChange(this.state.main_item[this.state.order_number].detail_title);
        const left = <div className={'main_left'}><div className={'main_left_number'}>{this.state.main_item[this.state.order_number].number}</div><div className={'main_left_kind'}>{kind}</div></div>;
        const picture = <div className={'main_picture'}><picture><img src={this.state.main_item[this.state.order_number].image}/>
        <source srcSet={this.state.main_item[this.state.order_number].image}/></picture></div>;
        const detail = <div className={'main_detail'}><div className={'main_detail_detail'}>{detail_detail}</div><div className={'main_detail_changebar'}><div className={"emptyBar"}><div className={"fillBar"}></div></div></div>
        <div className={'main_detail_title'}>{detail_title}</div></div>;
        const right = <div className={'main_right'}><div className={'main_right_nextPicture'}><picture><source/><img src={this.state.main_item[this.state.next_order_number].image}/></picture></div><div className={'main_right_fullStory'}>Read the full<br/>story</div></div>;

        const main = <div className={'main'}>{left}{picture}{detail}{right}</div>
        const items = <div className={'items'}><Item/></div>
        const div=<div>{main}{items}</div>

        return(div);
    }
}

export default Body;
