import React from 'react';

class Item extends React.Component{
    constructor(props){
        super(props);
        this.state={
            detail:"Bringing\nDaniel Costa's\nvision to life\nthrough space\ndesign.",
            date:"2019.02.24"
        }
    }
    render(){
        const detail=this.state.detail.splice('\n').map(s=>{return(<span>{s}<br/></span>)})
        const div = <div className={'item'}><div className={'item_kind'}></div><div className={'item_picture'}></div>
            <div className={'item_detail'}><div className={'item_detail_detail'}>{detail}</div><div className={'item_detail_date'}>{this.state.date}</div></div></div>;
        return(div);
    }
}

export default Item;
